/**
 * This class is used to display small visual icons in the header of a panel. There are a set of
 * 25 icons that can be specified by using the {@link #type} config. The {@link #handler} config
 * can be used to provide a function that will respond to any click events. In general, this class
 * will not be instantiated directly, rather it will be created by specifying the {@link Ext.Panel#tools}
 * configuration on the Panel itself.
 *
 *     @example
 *     Ext.create('Ext.Panel', {
 *         title: 'A Panel',
 *         fullscreen: true,
 *
 *         tools: [{
 *             type: 'help',
 *             handler: function() {
 *                 // show help here
 *             }
 *         }, {
 *             itemId: 'refresh',
 *             type: 'refresh',
 *             hidden: true,
 *             handler: function () {
 *                 // do refresh
 *             }
 *         }, {
 *             type: 'search',
 *             handler: function (panel) {
 *                 // do search
 *                 panel.down('#refresh').show();
 *             }
 *         }]
 *     });
 *
 * @since 6.0.1
 */
Ext.define('Ext.Tool', {
    extend: 'Ext.Component',
    alternateClassName: 'Ext.panel.Tool',
    xtype: ['tool', 'paneltool'],

    /**
     * @property {Boolean} isPanelTool
     * `true` in this class to identify an object as an instantiated Tool, or subclass thereof.
     * @readonly
     * @deprecated 6.5.0 Use `isTool` instead.
     */
    isPanelTool: true,

    /**
     * @property {Boolean} isTool
     * `true` in this class to identify an object as an instantiated Tool, or subclass thereof.
     * @readonly
     * @since 6.5.0
     */
    isTool: true,

    focusable: true,
    tabIndex: 0,
    focusEl: 'element',
    ariaEl: 'element',

    inheritUi: true,

    classCls: Ext.baseCSSPrefix + 'tool',
    pressedCls: Ext.baseCSSPrefix + 'pressed',
    hoveredCls: Ext.baseCSSPrefix + 'hovered',
    passiveCls: Ext.baseCSSPrefix + 'passive',

    element: {
        reference: 'element',
        tabIndex: 0,
        listeners: {
            click: 'onClick',
            mousedown: 'onMouseDown',
            mouseover: 'onMouseOver',
            mouseout: 'onMouseOut'
        },

        children: [{
            reference: 'iconElement',
            cls: Ext.baseCSSPrefix + 'icon-el ' + Ext.baseCSSPrefix + 'font-icon'
        }]
    },

    /**
     * @cfg {Function/String} handler
     * A function to execute when the tool is clicked.
     *
     * @cfg {Ext.Component} handler.owner The logical owner of the tool. In a typical usage
     * this will be an `Ext.Panel` (as specified by the `toolOwner` config).
     * @cfg {Ext.Tool} handler.tool The tool that is calling.
     * @cfg {Ext.event.Event} handler.event The click event.
     * @controllable
     */
    handler: null,

    /**
     * @cfg {Object} scope
     * The scope to execute the {@link #handler} function. Defaults to the tool.
     */
    scope: null,

    /**
     * @cfg {Ext.Component} toolOwner
     * The owner to report to the `handler` method. Default is `null` for the `parent`.
     * This is automatically set to the owning `Ext.Panel` when a tool is created as a
     * member of a panel's `tools`.
     */
    toolOwner: null,

    config: {
        /**
         * @cfg {String} iconCls
         * An alternative to the standard list of {@link #type types}.
         */
        iconCls: null,

        /**
         * @cfg {String} type
         * The type of tool to render. The following types are available:
         *
         * ##Neptune Theme
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> close
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> minimize
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> maximize
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> restore
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> toggle
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> gear
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> prev
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> next
         * - <img style="vertical-align:sub;margin-right:4px;" width="10" height="16" title="" alt="" src="data:image/gif;base64," /> pin
         * - <img style="vertical-align:sub;margin-right:4px;" width="10" height="16" title="" alt="" src="data:image/gif;base64," /> unpin
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> right
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> left
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> down
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> up
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> refresh
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> plus
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> minus
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> search
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="13" title="" alt="" src="data:image/gif;base64," /> save
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> help
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="14" title="" alt="" src="data:image/gif;base64," /> print
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="16" title="" alt="" src="data:image/gif;base64," /> expand
         * - <img style="vertical-align:sub;margin-right:4px;" width="16" height="14" title="" alt="" src="data:image/gif;base64," /> collapse
         */
        type: null,

        /**
         * @cfg {Boolean} [passive=false]
         * If set to `true` this Tool is display-only. It does not respond to mouseovers,
         * mousedowns, or clicks. It is not focusable, and calls no handlers.
         */
        passive: null
    },

    /**
     * @cfg {Boolean} stopEvent
     * Specify as false to allow click event to propagate.
     */
    stopEvent: true,
    
    weight: 10,

    updateIconCls: function (iconCls, oldValue) {
        this.iconElement.replaceCls(oldValue, iconCls);
    },

    updateType: function (type, oldType) {
        var me = this,
            baseCls = Ext.Tool.prototype.classCls,
            iconElement = me.iconElement;

        me.type = type;
        iconElement.replaceCls(oldType && (baseCls + '-type-' + oldType),
                          type && (baseCls + '-type-' + type));
    },

    updateDisabled: function (disabled, oldDisabled) {
        this.callParent([ disabled, oldDisabled ]);

        this.el.removeCls([ this.hoveredCls, this.pressedCls ]);
    },

    updatePassive: function (passive) {
        var me = this;

        me.setDisabled(passive);
        me.el.toggleCls(me.passiveCls, passive);
        me.el.setTabIndex(passive ? null : me.getTabIndex());
        me.focusable = !passive;
    },

    shouldRipple: function(e) {
        return !this.getPassive() && this.callParent();
    },

    privates: {
        //<debug>
        _toolTypes: {
            close:1,
            collapse:1,
            disclosure:1,
            down:1,
            expand:1,
            gear:1,
            help:1,
            left:1,
            maximize:1,
            minimize:1,
            minus:1,
            menu: 1,
            //move:1,
            next:1,
            pin:1,
            plus:1,
            prev:1,
            print:1,
            refresh:1,
            //resize:1,
            restore:1,
            right:1,
            save:1,
            search:1,
            toggle:1,
            unpin:1,
            up:1
        },
        //</debug>

        invokeToolHandler: function (me, handler, scope, args, ev) {
            if (handler) {
                Ext.callback(handler, me.scope, args, 0, me);
            }

            /**
             * @event click
             * Fires when the tool is clicked
             * @param {Ext.Tool} this
             * @param {Ext.event.Event} e The event object
             * @param {Ext.Component} owner The logical owner of the tool. In a typical
             * `Ext.Panel`, this is set to the owning panel. This value comes from the
             * `toolOwner` config.
             */
            me.fireEvent('click', me, ev, me.toolOwner || me.ownerCt);

            return true;
        },

        /**
         * Called when the tool element is clicked
         * @param {Ext.event.Event} ev
         * @private
         */
        onClick: function (ev) {
            var me = this,
                handler = me.handler || me.callback, // Pick up callback for classic compat
                toolOwner = me.toolOwner,
                args, invoker;

            if (me.getDisabled()) {
                return false;
            }

            if (me.stopEvent !== false) {
                ev.stopEvent();
            }

            args = [ me.parent, me, ev ];

            if (toolOwner) {
                args[0] = toolOwner;
                if (toolOwner.augmentToolHandler) {
                    toolOwner.augmentToolHandler(me, args);
                }
            }

            invoker = (toolOwner && toolOwner.invokeToolHandler) ? toolOwner : me;

            return invoker.invokeToolHandler(me, handler, me.scope, args, ev);
        },

        /**
         * Called when the user presses their mouse button down on a tool
         * Adds the press class ({@link #pressedCls})
         * @private
         */
        onMouseDown: function(e) {
            var me = this;
            if (!me.focusable) {
                e.preventDefault();
            }

            if (me.getDisabled()) {
                return false;
            }

            me.addCls(me.pressedCls);
            Ext.GlobalEvents.setPressedComponent(me, e);
        },

        /**
         * Called by {@link Ext.GlobalEvents#setPressedComponent} when the global
         * mouseup event fires and there's a registered pressed component.
         * @private
         */
        onRelease: function() {
            this.removeCls(this.pressedCls);
        },

        /**
         * Called when the user rolls over a tool
         * Adds the over class ({@link #hoveredCls})
         * @private
         */
        onMouseOver: function() {
            if (this.getDisabled()) {
                return false;
            }
            this.addCls(this.hoveredCls);
        },

        /**
         * Called when the user rolls out from a tool.
         * Removes the over class ({@link #hoveredCls})
         * @private
         */
        onMouseOut: function() {
            this.removeCls(this.hoveredCls);
        }
    }
});
